pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./OwnableContract.sol";

contract Bonus is Initializable, ReentrancyGuardUpgradeable, PausableUpgradeable, OwnableContract{

    using SafeMathUpgradeable for uint256;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    address public betCoin;

    // 全网用户当前总共质押了多少BET
    uint256 public allUserTotalPledgeAmount;

    // 参与质押的用户，数组会随着参与的用户将会越来越多
    uint256 public userCount;

    // 地址对应的编号
    mapping(address => uint256) public userToNumberMap;

    // 某用户当前总质押了多少币
    mapping(uint256 => uint256) public userTotalPledgeAmount;

    // 今天某用户质押了多少币
    mapping(uint256 => mapping(uint256 => uint256)) public userPledgeBetTodayMap;

    // 今天所有用户质押了多少币
    mapping(uint256 => uint256) public allUserPledgeBetTodayMap;

    // 用户可提取的分红
    mapping(uint256 => uint256) public withdrawBonus;

    function initialize(address _betCoin) public initializer {
        betCoin = _betCoin;
        __Ownable_init();
    }

    function pause() public whenNotPaused onlyAdmin{
        _pause();
    }

    function unPause() public whenPaused onlyAdmin{
        _unpause();
    }

    function updateBetCoin(address _betCoin) public onlyAdmin{
        betCoin = _betCoin;
    }

    function deposit(uint256 amount) public nonReentrant whenNotPaused{
        require(amount > 0, "amount is error.");
        uint256 number = userToNumberMap[msg.sender];
        if(number == 0){
            userCount++;
            userToNumberMap[msg.sender] = userCount;
        }
        IERC20Upgradeable erc20 = IERC20Upgradeable(betCoin);
        erc20.safeTransferFrom(address(msg.sender), address(this), amount);
        userTotalPledgeAmount[number] += amount;
        userPledgeBetTodayMap[today()][number] += amount;
        allUserPledgeBetTodayMap[today()] += amount;
        allUserTotalPledgeAmount += amount;
    }

    function withdrawPrincipal(uint256 amount) public nonReentrant whenNotPaused{
        require(amount > 0, "amount is error.");
        uint256 number = userToNumberMap[msg.sender];
        require(amount <= userTotalPledgeAmount[number], "amount upper limit exceeded.");
        IERC20Upgradeable erc20 = IERC20Upgradeable(betCoin);
        if(amount <= userPledgeBetTodayMap[today()][number]){
            userPledgeBetTodayMap[today()][number] -= amount;
        }else{
            userPledgeBetTodayMap[today()][number] = 0;
        }
        userTotalPledgeAmount[number] -= amount;
        allUserPledgeBetTodayMap[today()] -= amount;
        allUserTotalPledgeAmount -= amount;
        erc20.safeTransfer(msg.sender, amount);
    }

    function withdrawInterest() public nonReentrant whenNotPaused{
        uint256 bonus = withdrawBonus[userToNumberMap[msg.sender]];
        require(bonus == 0, "there are no withdrawable dividends.");
        IERC20Upgradeable erc20 = IERC20Upgradeable(betCoin);
        erc20.safeTransfer(msg.sender, bonus);
        withdrawBonus[userToNumberMap[msg.sender]] = 0;
    }

    function withdraw() public onlyOwner{
        IERC20Upgradeable erc20 = IERC20Upgradeable(betCoin);
        uint256 balance = erc20.balanceOf(address(this));
        erc20.safeTransfer(msg.sender, balance);
    }

    function shareBonus() public onlyAdmin nonReentrant whenNotPaused{
        IERC20Upgradeable erc20 = IERC20Upgradeable(betCoin);
        uint256 balance = erc20.balanceOf(address(this));
        require(balance > 0, "insufficient funds.");
        uint256 yesterdayPledgeAmount = allUserTotalPledgeAmount.sub(allUserPledgeBetTodayMap[today()]);
        if(yesterdayPledgeAmount > 0){
            uint256 singleCurrencyIncome = balance.div(yesterdayPledgeAmount);
            for(uint256 i=1; i<=userCount; i++){
                if(userTotalPledgeAmount[i] != 0){
                    withdrawBonus[i] += (userTotalPledgeAmount[i].sub(userPledgeBetTodayMap[today()][i])).mul(singleCurrencyIncome).div(1e18);
                }
            }
        }
    }

    // 2023-03-20 08:00:00 - 2023-03-21 07:59:59
    function today() internal view returns(uint256 dayIndex){
        dayIndex = block.timestamp / 86400;
    }
}
